package allprogram;

public class largestnumber3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=45,b=34,c=23;
		if(a>b)
		{
			if(a>c)
			{
				System.out.println("a is greater");
			}
			else
			{
				System.out.println("c is greater");
			}
		}
		else if(b>c)
		{
			System.out.println("b is greater");
		}
		else
		{
			System.out.println("c is greater");
		}

	}

}
