package allprogram;

public class relationaloperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=40;
		int b=100;
		//greater than
		System.out.println("b value is greater than a value :" +(b>a));
		//less than
		System.out.println("b value is less than a value: " +(b<a));
		//greator than equal to
		System.out.println("b value is greator than equal to a value: " +(b>=a));
		//less than equal to
		System.out.println(b<=a);
		//equal to equal to
		System.out.println(b==a); //compare value of two number
		//not equal to 
		System.out.println(a!=b);

	}

}
